import { useState } from 'react';

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState('');

  const enterNameIsValid = enteredName.trim() !== ''; 

  const nameInputChangeHandler = event => {
    setEnteredName(event.target.value);
    if (event.target.value.trim() !== '') {
      setEnterNameIsValid(true);
    }
  };

  const nameInputBlurHandler = event => {
    if (enteredName.trim() === '') {
      setEnterNameIsValid(false);
      return;
    }
  }

  const formSubmitHandler = event => {
    event.preventDefault();
    if (enteredName.trim() === '') {
      setEnterNameIsValid(false);
      return;
    }

    setEnterNameIsValid(true);
    console.log(enteredName);
    setEnteredName('');
  }

  const nameInputClasses = enterNameIsValid? "form-control" : "form-control invalid"

  return (
    <form onSubmit = {formSubmitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' 
        onChange={nameInputChangeHandler} 
        onBlur = {nameInputBlurHandler}
        value={enteredName} required/>
        {!enterNameIsValid && <p className="error-text">Input cannot be empty</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
