      fetch('https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyAb4PT8dGXplgAHjPAlbm9_oBj70Ks5v5I', 
      {
        method: 'POST',
        body: JSON.stringify({
          email: enteredEmail,
          password: enteredPassword,
          returnSecureToken: true
        }),
        headers: {
          'Content-Type': 'application/json'
        }
      }).then(res => {
        if (res.ok) {

